sys_hub_input_scripts.script:
{"resource_path":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
var resPath = "/api/files";
if(fd_data.action_inputs.location.length > 0)
{
 resPath+="/" + fd_data.action_inputs.location;
}

resPath+= "?recursive=" + fd_data.action_inputs.recursive;

return resPath;"}}

sys_variable_value.value:
(function execute(inputs, outputs) {
		// Only parse the response body if the status code is 200	
    	if (inputs.status_code == '200' || inputs.status_code == '201'){

          	// Parse the response_body input variable and save parsed object as responseBody
          	var responseBody = JSON.parse(inputs.response_body);
          	var fileCount = responseBody.length;

          	// Map the parsed responseBody values to output variables
          	outputs.file_count = fileCount;
      }
})(inputs, outputs);

