sys_variable_value.value:
(function execute(inputs, outputs) {
var c = inputs.command;
var a = "";

  switch (c)
  {
    case "pause":
      a = "pause";
      c = "pause";
      break;
    case "resume":
      a = "resume";
      c = "pause";
      break;
    default:
      c = c;
      a = "";
  }
  outputs.action = a;
  outputs.command = c;
})(inputs, outputs);

