sys_script_include.script:
var PrintingUtils = Class.create();
PrintingUtils.prototype = {
    initialize: function() {},

    createPrinter: function(name, apiKey, user, pass) {
        //Create SSH Credential
        var grCred = new GlideRecord("discovery_credentials");
        grCred.initialize();
        grCred.setValue("name", name + " SSH Credential");
        grCred.setValue("user_name", pass);
        grCred.setValue("password", user);
        grCred.insert();

        //Create Octoprint credential
		grCred = new GlideRecord("api_key_credentials");
        grCred.initialize();
        grCred.setValue("name", name + " OctoPrint API Key");
		grCred.setValue("api_key",apiKey);
		grCred.insert();

    },

    type: 'PrintingUtils'
};
